    reviews_data = []
    total_sentiment_score = 0
    total_reviews = 0
    for url in urls:
        response = requests.get(url)
        soup = BeautifulSoup(response.text, 'html.parser')
        reviews = soup.find_all('div', {'class': 'ZmyHeo'})
        reviews_for_url = []
        for review in reviews[:15]:  # Limiting to 5 reviews for demonstration purposes
            text = review.text
            text = ' '.join(text.split())
            text = ''.join(e for e in text if e.isalnum() or e.isspace())
            sentiment_score = TextBlob(text).sentiment.polarity
            total_sentiment_score += sentiment_score
            total_reviews += 1
            sentiment = ""
            if sentiment_score > 0.3:
                sentiment = "Positive"
            elif sentiment_score < 0.2:
                sentiment = "Negative"
            else:
                sentiment = "Neutral"
            reviews_for_url.append({'text': text, 'sentiment': sentiment})
        reviews_data.append({'url': url, 'reviews': reviews_for_url})
    if total_reviews != 0:
        average_sentiment_score = total_sentiment_score / total_reviews
    else:
        average_sentiment_score = 0
    return render_template('s1.html', reviews_data=reviews_data, average_sentiment_score=average_sentiment_score)
